import React from 'react'

export default function Pages({TotalPages, PostsPerPage, setPageNumber }) {
    
    let pages = [];
    for(let i = 1; i <= Math.ceil(TotalPages / PostsPerPage) ; i++ )
    {
        pages.push(i);
    }

    pages = pages.map(p=>{
        return(
            <button key={p} href="#" onClick={()=>setPageNumber(p)}> {p}</button>
        )
    });
    
    return (
        <div>
            {pages}
        </div>
    )
}
