import React, { Component } from 'react'
import {connect} from 'react-redux'

 class Post extends Component {

    // constructor(props){
    //     super(props);
    //     this.state = {
    //         post : null
    //     }
    // }

    // componentDidMount(){
    //     fetch("https://jsonplaceholder.typicode.com/posts/" + this.props.match.params.post_id )
    //       .then((response) => response.json())
    //       .then((json) => this.setState({
    //           post : json
    //       }));
    // }
  
    render() {
        if(this.props.post == null){
            return (
                <div className="red-text">
                    Not Found.
                </div>
            )
        }
        let {post} = this.props;
        let p = post;
        return (
            <div className="container">
             <div className="row" key={p.id}>
                <div className="card blue darken-1">
                  <div className="card-content white-text">
                    <b>                     
                     <a className="yellow-text" href={"/" + p.id}> <span className="card-title">{p.title}</span></a>
                    </b>
                    <p>{p.body}</p>
                  </div>
                </div>
              </div>
                
            </div>
        )
    }
}

const mapStateToprops = (state, ownProps) => {
    let p = state.posts.filter((p)=> {return p.id == 1  })
    console.log(ownProps.match.params.post_id );
    return {
        post : p
    }
}

export default connect(mapStateToprops)(Post)
